import './App.css';
import React from "react";
import {Component} from "react";
import {BrowserRouter as Router, Redirect, Route} from "react-router-dom";
import {Navigate} from 'react-router-dom';
import LibraryService from "../../repository/libraryRepository";
import Books from "../Book/BookList/books";
import BookAdd from "../Book/BookAdd/bookAdd";
import BookEdit from "../Book/BookEdit/bookEdit";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      books: [],
      categories: [],
      authors: [],
      selectedBook: {},
      countries: []
    }
  }

  render() {
    return (
        <Router>
            <Header/>
            <main>
          <Redirect>
            <Route path={"/books"} exact render={
              <Books books={this.state.books}
                     onDelete={this.deleteBook}
                     onEdit={this.getBook}
                     onMarkAsTaken={this.markAsTaken}/>
            }/>


            <Route path={"/books/add"} exact element={
              <BookAdd categories={this.state.categories}
                       authors={this.state.authors}
                       onAddBook={this.addBook}/>}/>

            <Route path={"/books/edit/:id"} exact element={
              <BookEdit categories={this.state.categories}
                        authors={this.state.authors}
                        onEditBook={this.editBook}
                        book={this.state.selectedBook}/>}/>

          </Redirect>
          {/*<Navigate to={"/books"}/>*/}

        </main>
        </Router>
        // <div>
        //     <Books books={this.state.books}/>
        // </div>
    );
  }

  componentDidMount() {
    this.loadCountries();
    this.loadAuthors();
    this.loadCategories();
    this.loadBooks();
    // this.loadBooksPaginated();
  }
  loadBooksPaginated = () => {
      LibraryService.fetchPaginatedBooks()
        .then((data) => {
          this.setState({
            books: data.data
          });
        });
  }
  loadCategories = () =>{
      LibraryService.fetchCategories()
        .then((data) => {
          this.setState({
            categories: data.data
          });
        });
  }
  loadBooks = () => {
      LibraryService.fetchBooks()
        .then((data) => {
          this.setState({
            books: data.data
          });
        });
  }
  loadAuthors = () => {
      LibraryService.fetchAuthors()
        .then((data) => {
          this.setState({
            authors: data.data
          });
        });
  }
  loadCountries = () => {
      LibraryService.fetchCountries()
        .then((data) => {
          this.setState({
            countries: data.data
          });
        });
  }
  deleteBook = (id) => {
      LibraryService.deleteBook(id)
        .then(() => {
          this.loadBooks();
        });
  }
  addBook = (name, category, authorId, availableCopies) => {
    const newbook = {name: name, category: category, authorId: authorId, availableCopies: availableCopies};
      LibraryService.addBook(newbook)
        .then(() => {
          this.loadBooks();
        });
  }
  editBook = (id, name, category, authorId, availableCopies) => {
      LibraryService.editBook(id, name, category, authorId, availableCopies)
        .then(() => this.loadBooks());
  }
  getBook = (id) => {
      LibraryService.getBook(id)
        .then((data) => this.setState({selectedBook: data.data}))
  }
  markAsTaken = (id) => {
      LibraryService.markAsTaken(id)
        .then(() => {
          this.loadBooks()
        });
  }


}


export default App;
